function checkBoxClicked(n){updateGroupCount(n.target.getAttribute("subgroup"));updateTotals()}function updateGroupCount(n){const t=document.querySelectorAll(`.nhsuk-checkboxes__input[subgroup=${n}]:checked`).length,i=t>0?` - ${t} selected`:"";document.querySelector(`.counter-class[subgroup=${n}]`).innerText=i}function updateTotals(){const n=document.querySelectorAll(".nhsuk-checkboxes__input:checked").length,t=document.querySelectorAll(".nhsuk-checkboxes__input").length;Array.from(document.querySelectorAll(".selection-counter")).forEach(i=>i.innerText=`${n} out of ${t} selected`)}window.onload=function(){updateTotals();Array.from(document.querySelectorAll(`.nhsuk-checkboxes__input:checked`)).map(n=>n.getAttribute("subgroup")).filter((n,t,i)=>i.indexOf(n)===t).forEach(n=>updateGroupCount(n));Array.from(document.querySelectorAll(".selection-counter")).forEach(n=>n.removeAttribute("style"));Array.from(document.querySelectorAll(".nhsuk-checkboxes__input")).forEach(n=>n.addEventListener("change",checkBoxClicked,!1))};