function checkBoxClicked(e){updateGroupCount(e.target.getAttribute("subgroup")),updateTotals()}function updateGroupCount(e){const t=document.querySelectorAll(`.nhsuk-checkboxes__input[subgroup=${e}]:checked`).length,o=t>0?` - ${t} selected`:"";document.querySelector(`.counter-class[subgroup=${e}]`).innerText=o}function updateTotals(){const e=document.querySelectorAll(".nhsuk-checkboxes__input:checked").length,t=document.querySelectorAll(".nhsuk-checkboxes__input").length;Array.from(document.querySelectorAll(".selection-counter")).forEach((o=>o.innerText=`${e} out of ${t} selected`))}window.onload=function(){updateTotals(),Array.from(document.querySelectorAll(".nhsuk-checkboxes__input:checked")).map((e=>e.getAttribute("subgroup"))).filter(((e,t,o)=>o.indexOf(e)===t)).forEach((e=>updateGroupCount(e))),Array.from(document.querySelectorAll(".selection-counter")).forEach((e=>e.removeAttribute("style"))),Array.from(document.querySelectorAll(".nhsuk-checkboxes__input")).forEach((e=>e.addEventListener("change",checkBoxClicked,!1)))};